  def findBasement(floor: Int, moves: List[Char]): Int = {
    if (floor == -1)
      0
    else
      moves match {
        case h :: rest if (h == '(') => 1 + findBasement(floor + 1, rest)
        case h :: rest if (h == ')') => 1 + findBasement(floor - 1, rest)
      }
  }

